import IStrategy from "../../interface/IConcreteStrategy";
import IUnit from "../../interface/IUnit";
export default abstract class Unit implements IUnit {
  abstract _unitType: string;
  abstract _HP: number;
  abstract _damage: number;
  abstract _initiative: number;
  abstract _actionType: void;
  abstract _imgPath: string;
  abstract _id: number | null;

  private strategy: IStrategy;
  constructor(strategy: IStrategy,id:number) {
    this.strategy = strategy;
    this.__id
  }
  // abstract _HP: number;

  doDefenseAction(incomingDamage: number, curentHP: number) {
    // const reducedIncomingDamage: number = Math.ceil(incomingDamage * 0.5);
    // this._HP = curentHP - reducedIncomingDamage;
    console.log("defense");
  }
  doHPreduce(incomingDamage: number, curentHP: number) {
    // if (incomingDamage >= curentHP) {
    //   this._HP = 0;
    // } else {
    //   this._HP = curentHP - incomingDamage;
    // }
    this._HP -= 30;
    if (this._HP < 0) this._HP = 0;
    return 30;
  }
  setAgentTarget() {}
  showInfo() {
    console.log(this);
  }

  getHP() {
    return this._HP;
  }

  public doAction(position: number): void {
    this.strategy.doAlgorithm(position);
  }
  public doSelect(): void {
    this.strategy.doTargetSelection();
  }
}
