import IStrategy from "../../interface/IConcreteStrategy";
import Unit from "../unit/Unit";
export default class MeleeStrategy implements IStrategy {
  public doAlgorithm(position: number): void {
    console.log("Melee strategy", position);
  }
  public doTargetSelection(unit: Unit, battleField: Array<Unit>): Array<Unit> {
    console.log("Melee target selection attention");
    console.log(unit, battleField);
    console.log("unit in queue");
    const units = [unit, unit];
    return units;
  }
}
