import React from "react";
import "./App.css";
// import { CenterPart } from "./page/CenterPart";
import { RightPart } from "./page/RightPart";
import { LeftPart } from "./page/LeftPart";
import { CenterPartV2 } from "./page/CenterPartV2";
import { UnitsArray } from "./Logic/GenerateUnits";
import Unit from "../src/strategy/classes/unit/Unit";
import { useState, useEffect } from "react";
import { QueueUI } from "../src/page/QueueUI";

const App: React.FC = () => {
  const unitsHP = UnitsArray.map(unit => {
    return unit._HP;
  });
  const [HP, setHP] = useState(unitsHP);
  const handleClick = (index?: number): number => {
    (index===0) ? console.log(index) : console.log(123);
    return 1;
  };

  // const renderLeftPart = () => {

  //   const leftPart = UnitsArray.map((unit, index) => {
  //     renderCell();
  //   })
  //   console.log(leftPart);
  //   return leftPart;
  // };

  type props = {
    index: number;
  };
  const renderCell = () => {
    const unitsArrayUI: any = UnitsArray.map((unit, index) => {
      return (
        <QueueUI
          key={index}
          currentUnit={unit}
          currentIndex={index}
          onClick={() => {
            handleClick(index);
          }}
        />
      );
    });

    return unitsArrayUI;
  };

  return (
    <div className="app">
      {/* <LeftPart
        units={UnitsArray}
        onClick={() => {
          handleClick();
        }}
      /> */}
      <div className="left-part">{renderCell()}</div>;
      <CenterPartV2 units={UnitsArray} />
      <RightPart />
    </div>
  );
};

export default App;
