import React, { FunctionComponent, useEffect, useState } from "react";
import Unit from "../strategy/classes/unit/Unit";
import defense from "../../src/img/18-512.png";

interface UnitUIProps {
  currentIndex: number;
  currentUnit: Unit;
  ebuchka: number;
}
export const UnitUI: FunctionComponent<UnitUIProps> = ({
  currentIndex,
  currentUnit,
  ebuchka
}) => {
  const [currentHP, setCurrentHP] = useState(currentUnit._HP);
  const hp = currentUnit.getHP();

  return (
    <React.Fragment>
      <div className="cell">
        <div className="cell__unit-info">
          <img
            onClick={() => {
              console.log(currentUnit);
            }}
            className="cell__unit-info__image"
            src={currentUnit._imgPath}
            alt="img"
          />
          <div className="cell__unit-info__options">
            <div className="cell__unit-info__options__defense">
              <button
                onClick={() => {
                  if (ebuchka != currentUnit._HP) {
                    console.log("aga");
                  }
                currentUnit.doHPreduce(1, 1);
                  setCurrentHP(currentHP - 40);
                  console.log(ebuchka);
                }}
              >
                <img src={defense} width={40} alt="img" />
              </button>
            </div>
          </div>
        </div>
        <div className="cell__HP">
          <progress
            className="cell__HP__bar"
            value={currentHP}
            // value={currentUnit.getHP()}
            max={currentUnit._HP}
          ></progress>
        </div>
      </div>
    </React.Fragment>
  );
};
