import IStrategy from "../../interface/IConcreteStrategy";
import Unit from "../unit/Unit";
export default class MeleeStrategy implements IStrategy {
  public doAlgorithm(
    atackingUnit: number,
    target: number,
    targets: number[],
    battleField: Array<Unit>,
    HP: number[]
  ): number[] {
    console.log("Melee strategy");
    return HP;
  }
  public doTargetSelection(unit: Unit, battleField: Array<Unit>): Array<Unit> {
    if (unit._id > 5) {
      const enemies: Array<Unit> = battleField.slice(0, 6);
      const allies: Array<Unit> = battleField.slice(6, 12);
      console.log("enemies", enemies);
      console.log("allies", allies);
      const alliesFirstLane: Array<Unit> = allies.slice(0, 3);
      const alliesSecondLane: Array<Unit> = allies.slice(3, 6);
      const enemiesSecondLane: Array<Unit> = allies.slice(0, 3);
      const enemiesFirstLane: Array<Unit> = allies.slice(3, 6);
      console.log("allies first lane", alliesFirstLane);
      console.log("allis second lane", alliesSecondLane);
      alliesFirstLane.forEach((e, index) => {
        if (e._id === unit._id) {
          if (index == 1) {
            return ;
          }
        } else {
          console.log("in second Lane");
        }
      });

      return enemies;
    } else {
      const enemies: Array<Unit> = battleField.slice(6, 12);
      const allies: Array<Unit> = battleField.slice(0, 6);
      console.log("enemies", enemies);
      console.log("allies", allies);
      const alliesSecondLane: Array<Unit> = allies.slice(0, 3);
      const alliesFirstLane: Array<Unit> = allies.slice(3, 6);
      console.log("allies first lane", alliesFirstLane);
      console.log("allis second lane", alliesSecondLane);
      return enemies;
    }
  }
}
