    //     skipTurn();
    //   } else {
    //     const targetsForAction: Array<Unit> = unit.doSelect(UnitsArray);

        const a: number[] = [];
        const idTargets: number[] = targetsForAction.map(unit => {
          a.push(unit._id);
          return unit._id;
        });
        setTarget(idTargets);
