    // if (target.indexof(index)) return true;
    // else return false;
    if (target === undefined) {
    } else {
      if (target.indexOf(index) === -1 || HP[index] <= 0) {
        return false;
      } else return true;
    }
  };

  const renderCenterPart = () => {
    const unitsArrayUI: any = UnitsArray.map((unit, index) => {
