          hexed={isHexed[index]}
          underAtack={isAtcaked(index, unit)}
          onClick={() => {
            handleClick(index);
          }}
          onDefenseClick={() => {
            handleDefenseClick(index);
          }}
        />
      );
    });

    return <div className="battleField">{unitsArrayUI}</div>;
  };

  return (
    <div className="app">
      <div className="left-part">{renderLeftPart()}</div>

      <React.Fragment>{renderCenterPart()}</React.Fragment>
      <RightPart />
    </div>
  );
};

export default App;
