import IStrategy from "../../interface/IStrategy";
import DefaultActions from "../../interface/IUnitDefaultAction";
export default class Context implements DefaultActions {
  private strategy: IStrategy;

  constructor(strategy: IStrategy) {
    this.strategy = strategy;
  }
  // abstract _HP: number;

  doDefenseAction(incomingDamage: number, curentHP: number) {
    const reducedIncomingDamage: number = Math.ceil(incomingDamage * 0.5);
    this._HP = curentHP - reducedIncomingDamage;
  }
  setAgentTarget() {}
  doHPreduce(incomingDamage: number, curentHP: number) {
    if (incomingDamage >= curentHP) {
      this._HP = 0;
    } else {
      this._HP = curentHP - incomingDamage;
    }
  }

  public doAction(): void {
    this.strategy.doAlgorithm();
  }
}
