              <button
                onClick={() => {
                  props.onClick();
                }}
              >
                <img src={defense} width={40} alt="img" />
              </button>
            </div>
          </div>
        </div>
        <div className="cell__HP">
          {/* <progress
            className="cell__HP__bar"
            value={props.HP}
            // value={currentUnit.getHP()}
            max={props.currentUnit._HP}
          ></progress> */}
          {props.HP}
        </div>
      </div>
    </React.Fragment>
  );
};
