import Unit from "../strategy/classes/unit/Unit";
export const generateQueue: any = (units: Array<Unit>) => {
  let copy: Array<Unit> = Object.assign([], units);
  const sortedHPcopy = copy.map(unit => {
    if (unit._HP != 0) return unit;
  });

  sortedCopy.sort((a, b) => {
    if (a._initiative < b._initiative) {
      return 1;
    }
    if (a._initiative > b._initiative) {
      return -1;
    }
    return 0;
  });
  return copy;
};
