import React from "react";
import logo from "./logo.svg";
import "./App.css";
import centeaur from "./img/Centaur_Warrunner_icon.png";
import skelet from "./img/Skeleton_King_icon.png"
import bandit from "./img/Mirana_icon.png"
import elf from "./img/Enchantress_icon.png"

const App: React.FC = () => {
  class Square extends React.Component {
    render() {
      return (
        <button className="square">
          <img src={kittenHeader} width="120px" height="120px" />
        </button>
      );
    }
  }

  class Board extends React.Component {
    simpleArray: string[] = [];
    renderSquare(i: number) {
      return <Square />;
    }
    render() {
      return (
        <div>
          <div className="board-row">
            {this.renderSquare(0)}
            {this.renderSquare(1)}
            {this.renderSquare(2)}
          </div>
          <div className="border board-row">
            {this.renderSquare(3)}
            {this.renderSquare(4)}
            {this.renderSquare(5)}
          </div>
          <div></div>
          <div className="board-row">
            {this.renderSquare(6)}
            {this.renderSquare(7)}
            {this.renderSquare(8)}
          </div>
          <div className="board-row">
            {this.renderSquare(9)}
            {this.renderSquare(10)}
            {this.renderSquare(11)}
          </div>
        </div>
      );
    }
  }

  class Game extends React.Component {
    render() {
      return (
        <div className="game">
          <div className="game-board">
            <Board />
          </div>
        </div>
      );
    }
  }

  return (
    <div className="app">
      <div className="left-side">turn order</div>
      <div className="main-part">
        <div className="game-header">Battlefield</div>
        <Game />
      </div>
      <div className="right-side">game-info</div>
    </div>
  );
};

export default App;
