import React from "react";
import "./App.css";
import centeaur from "./img/Centaur_Warrunner_icon.png";
import skelet from "./img/Skeleton_King_icon.png";
import bandit from "./img/Mirana_icon.png";
import elf from "./img/Enchantress_icon.png";
import skeletMage from "./img/Lich_icon.png";
import archiMage from "./img/Void_Spirit_icon.png";
import healer from "./img/Nature's_Prophet_icon.png";
import hexer from "./img/Naga_Siren_icon.png";

const App: React.FC = () => {
  interface InjectedCounterProps {
    unit: any;
  }
  class Square extends React.Component<InjectedCounterProps> {
    render() {
      console.log(this.props.unit);
      return (
        <div>
          <button className="square">
            <img src={this.props.unit.centeaur} />
          </button>
          ;
        </div>
      );
    }
  }

  class Board extends React.Component {
    simpleArray: any = [
      centeaur,
      skelet,
      bandit,
      elf,
      skeletMage,
      archiMage,
      healer,
      hexer
    ];
    renderSquare(i: number) {
      return <Square unit={this.simpleArray} />;
    }
    render() {
      return (
        <div>
          <div className="board-row">
            {this.renderSquare(0)}
            {this.renderSquare(1)}
            {this.renderSquare(2)}
          </div>
          <div className="border board-row">
            {this.renderSquare(3)}
            {this.renderSquare(4)}
            {this.renderSquare(5)}
          </div>
          <div></div>
          <div className="board-row">
            {this.renderSquare(6)}
            {this.renderSquare(7)}
            {this.renderSquare(8)}
          </div>
          <div className="board-row">
            {this.renderSquare(9)}
            {this.renderSquare(10)}
            {this.renderSquare(11)}
          </div>
        </div>
      );
    }
  }

  class Game extends React.Component {
    render() {
      return (
        <div className="game">
          <div className="game-board">
            <Board />
          </div>
        </div>
      );
    }
  }

  return (
    <div className="app">
      <div className="left-side">turn order</div>
      <div className="main-part">
        <div className="game-header">Battlefield</div>
        <Game />
      </div>
      <div className="right-side">game-info</div>
    </div>
  );
};

export default App;
