import IStrategy from "../../interface/IConcreteStrategy";
import Unit from "../unit/Unit";
export default class ArcherStrategy implements IStrategy {
  public doAlgorithm(position: number): void {
    console.log("Archer strategy ", position);
  }
  public doTargetSelection(unit: Unit, battleField: Array<Unit>): Array<Unit> {
    console.log(" Archer target selection");
    console.log("unit in queue");
    const copyBattleField: Array<Unit> = [...battleField];
    const teamA = copyBattleField.slice(0, 5);
    const teamB = copyBattleField.slice(6, 12);
    if (unit._id < 6) {
      console.log(teamB);
      return teamB;
    } else return teamA;

    const units = [unit, unit];
    return units;
  }
}
